{
  allMarkdownRemark(
    limit: 1000,
    sort: { order: DESC, fields: [fields___prefix] },
    filter: {
      fields: {
        prefix: { regex: "/[0-9]{4}.*/" },
        slug: { ne: null }
      }
    }
  ) {
    edges {
      node {
        excerpt
        html
        fields {
          slug
          prefix
        }
        frontmatter {
          title
        }
      }
    }
  }
}

