query PostsByTag(
  $tag: String
) {
  allMarkdownRemark(limit: 1000, sort: {fields: [fields___prefix], order: DESC}, filter: {frontmatter: {tags: {in: [$tag]}}}) {
    totalCount
    edges {
      node {
        fields {
          slug
        }
        excerpt
        timeToRead
        frontmatter {
          title
          tags
        }
      }
    }
  }
}

query PostBySlug(
  $slug: String!
) {
  post: markdownRemark(fields: {slug: {eq: $slug}}) {
    id
    html
    htmlAst
    fields {
      slug
      prefix
    }
    frontmatter {
      title
      author
      tags
      cover {
        childImageSharp {
          resize(width: 300) {
            src
          }
        }
      }
    }
    parent {
      __typename
      ... on File {
        modifiedTime(formatString: "YYYY-MM-DD")
      }
    }
  }
  authornote: markdownRemark(fileAbsolutePath: {regex: "/author/"}) {
    id
    html
    htmlAst
  }
}

query PageByPath(
  $slug: String!
) {
  page: markdownRemark(fields: {slug: {eq: $slug}}) {
    id
    html
    htmlAst
    frontmatter {
      title
    }
  }
}

